function templateFunc( arr, settings ) {
	var result = "";
	var templateFilename = "chromatic-template.ly";

	// console.error( 'settings', settings );

	if ( ! settings ) 
		settings = {
			tempo : 130,
		};

	// console.error( 'default settings', settings );
	function js2scm( k ) {
		k = k.replace( /([A-Z][a-z0-9]*)/g, (s0,s1)=>"-" + s1.toLowerCase() );
		k = k.replace( /^-/, '' );
		return k;
	}

	function getSetting() {
		var s = '';
		for ( var i in settings ) {
			var matched = /^(scm)(.*)$/.exec( i );
			if ( matched ) {
				switch ( typeof settings[i] ) {
					case "string" :
					case "number" :
						s += `( ${js2scm(matched[2])} . ${settings[i]} )`;
						break;
					case "boolean" :
						s += `( ${js2scm(matched[2])} . ${settings[i] ? '#t' : '#f' } )`;
						break;
					default:
				}
			}
		}
		// console.error( '*********', `'(${s})` );
		return `'(${s})`;
	}

result += `
\\version "2.18.2"
\\include "aaron.ly"
\\include "${templateFilename}"
\\include "lilypond-book-preamble.ly"
\\language "aaron"

`;

	function createBlock( caption, comment, notes, lyrics ) {
		var result =  "";
		if ( caption ) {
			result +=
(`
\\markup \\bold \\italic {
    ${caption}
}
`);
		}

		if ( comment ) {
			let es = comment.split( /\n/ );
			for ( let i=0; i<es.length; i++ ) {
				result += "    %" + es[i] + "\n";
			}
		}

		result +=
`
\\makescore 
  \\relative do' {
    ${notes}
  }
  #${getSetting()}
`;
		return result;
	}

	for ( var i=0; i<arr.length; i++ ) {
		var e= arr[i];
		result += createBlock( e.caption, e.comment, e.notes, e.lyrics );
	}
	return result.trim();
}


	/////////////////////////////////////////////
	//
	//
	//
	/////////////////////////////////////////////

	var ch = require( 'chromatic' );
	function transpose( value ) {
		return ch.transpose( value ).join( " " )
	}

	function toNotes_pre( s ) {
		s = s.replace( /\n/gm, ' \\bar"" \\break ' );
		return s;
	}
	function toNotes( s ) {
		// console.error( s );
		s = s.replace( /\|/g,  '\\bar "|" ' );
		s = s.replace( /\n/gm, ' \\bar "" \\break ' );
		// console.error( s );
		return s;
	}
	function toLyrics( s ) {
		// Removing 's'
		s = s.replace( /(\s*\bs[0-9]*\b\s*)/g, ' ' );
		// Replacing ' ,
		s = s.replace( /[\'\,]/g, " " );
		// Replacing '< ... >'
		s = s.replace( /\<(.*?)\>/g, ( s0, s1 ) => {
			s1 = s1.split( / +/ ).reverse().join( " " ).trim();
			return "\\markup{   \\override #\'(baseline-skip . 2.5) \\center-column{ " + s1 + "  } } " 
		});
		// Replacing '[ ... ]'
		s = s.replace( /[\[\]]/g, " " );
		return s;
	}

	//
	// noteArrays = [
	//	  "do do re mi fa",
	//	  [ 'do', 'do', 're', 'mi', ],
	//	  [ 'do', 'do', 're', 'mi', ],
	// ] 
	// => This bocomes a file contains three scores.
	//
	function applyTemplate( noteArrays, settings ) {
		if ( Array.isArray( noteArrays ) ) {
		} else {
			noteArrays = [ noteArrays ] ;
		}

		var result = [];
		for ( var i=0; i< noteArrays.length; i++ ) {
			var s = noteArrays[i];
			s = toNotes_pre( s );
			s = transpose( s );
			s = toNotes( s );
			result[i] = {
				notes : s,
				lyrics : toLyrics( s  ),
			};
		}
		return templateFunc( result, settings );
	};

if ( module && module.exports ) {
	module.exports.templateFunc = templateFunc;
	module.exports.applyTemplate = applyTemplate;
} else {
	let content = '';
	process.stdin.resume();
	process.stdin.on('data', function(buf) { content += buf.toString(); });
	process.stdin.on('end', function() {
		console.error( content );
		process.stdout.write( templateFunc( new Function( content ).apply() ) );
		process.stdout.write( "\n" );
	});
}

