var __defaultLilypondCommandLine = "lilypond";

var o = {
	__defaultLilypondCommandLine : __defaultLilypondCommandLine,
	__lilypondCommandLine : __defaultLilypondCommandLine,
	getLilypondCommandLine : function() {
		if ( o.__lilypondCommandLine == __defaultLilypondCommandLine ) {
			console.error( "lilyutils.js : [WARNING] __lilypondCommandLine is not configured." );
		}
		return o.__lilypondCommandLine;
	},
	commandInterface : function() {
		console.log( o.__lilypondCommandLine );
	},

	globalSettings : function( __settings ) {
		if ( __settings.__lilypondCommandLine )
			o.__lilypondCommandLine = __settings.__lilypondCommandLine;
	},
};

module.exports = o;

if ( module.require == module.main ) {
	o.commandInterface();
}
