function createTemplate( arr, templatePath ) {
	var result = "";
	if ( ! templatePath ) 
		templatePath = "chromatic-solfege-gen.ly";

result += `
\\version "2.18.2"
\\include "aaron.ly"
\\include "${templatePath}"
\\include "lilypond-book-preamble.ly"
\\language "aaron"

`;

	function createBlock( caption, notes, lyrics ) {
		var result =  "";
		if ( caption ) {
			result +=
(`
\\markup \\bold \\italic {
    ${caption}
}
`);
		}

		result +=
`
\\makescore 
  \\relative do' {
    ${notes}
  }
  \\lyricmode {
    ${lyrics}
  }
`;
		return result;
	}

	for ( var i=0; i<arr.length; i++ ) {
		var e= arr[i];
		result += createBlock( e.caption, e.notes, e.lyrics );
	}
	return result.trim();
}

if ( module && module.exports ) {
	module.exports = createTemplate;
}
